import groq from 'groq'
import Fullscreen from '../components/Fullscreen'
import styled from "@emotion/styled";
import client from '../client'
import ArticleCard from '../components/ArticleCard'
import imageUrlBuilder from '@sanity/image-url'
import Link from 'next/link';


function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}

const Blogfront = styled.div`
  margin: 0 auto;
  max-width: 1280px;
  padding: 3rem 1rem 1rem 1rem;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: center;

  @media (min-width: 900px) {
    flex-direction: row;
    justify-content: center;
    align-items: flex-start;
    flex-wrap: wrap;
        }

    h2{
        padding-bottom: 1rem;
        text-align: center;
    }
    p{
      text-align: center;
      font-size: 1.8rem;
      line-height: 1.6;
      max-width: 700px;
      margin: 3rem 2rem 2rem 2rem;
    }
    a:hover{
        color: turquoise;
    }
`;



function SurfeTips(props) {
  const { posts = [] } = props
  return(
<>
<Fullscreen image="/img/waves.jpg"  height="30vh" >
    <h1>Surfe-tips</h1>
    </Fullscreen> 
    <Blogfront>
        {posts.map(
          ({ _id, title = '', slug = '', mainImage = '' }) =>
            slug && (
              <li key={_id}>
              <Link href={`${slug.current}`} as={`/post/${slug.current}`}>

                <ArticleCard 
                bildeURL={urlFor(mainImage.asset._ref).width(1000).url()}
                title={title}
                />  

              </Link>
              </li>
            )
        )}
      </Blogfront>
</>
  )


}


SurfeTips.getInitialProps = async () => ({
  posts: await client.fetch(groq`
    *[_type == "post" && publishedAt < now()]|order(publishedAt desc)
  `)
})



export default SurfeTips
